package net.ting.sliding;import android.annotation.SuppressLint;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.TextView;public class SettingBottomAdaper extends BaseAdapter {	private LayoutInflater inflater;	public String[] SETTINGTOP_NAME;	public boolean isVisible = false;	private Context mContext;	public SettingBottomAdaper(Context mContext) {		this.mContext = mContext;		inflater = (LayoutInflater) mContext.getSystemService(Context.LAYOUT_INFLATER_SERVICE);		SETTINGTOP_NAME = mContext.getResources().getStringArray(R.array.setting_list);	}	@Override	public int getCount() {		return SETTINGTOP_NAME.length;	}	public void setVersionVISIBLE(boolean isVisible) {		this.isVisible = isVisible;		notifyDataSetChanged();	}	@Override	public Object getItem(int position) {		return SETTINGTOP_NAME[position];	}	@Override	public long getItemId(int position) {		return position;	}	@SuppressLint("InflateParams")	@Override	public View getView(final int position, View convertView, ViewGroup parent) {		ViewHolder holder = null;		if (convertView == null) {			holder = new ViewHolder();			convertView = inflater.inflate(R.layout.setting_item, null);			holder.setting_bottom_text = (TextView) convertView.findViewById(R.id.setting_bottom_text);			convertView.setTag(holder);		} else {			holder = (ViewHolder) convertView.getTag();		}		holder.setting_bottom_text.setText(SETTINGTOP_NAME[position]);		return convertView;	}	private class ViewHolder {		private TextView setting_bottom_text;		private TextView new_version;	}}