package net.ting.sliding;import android.annotation.SuppressLint;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.ImageView;import android.widget.TextView;public class SlideMenuAdaper extends BaseAdapter {	private LayoutInflater inflater;	public String[] SETTINGTOP_NAME;	public int[] SETTINGTOP_IMAGE = { R.drawable.sliding_wash_control, R.drawable.sliding_detergent,			R.drawable.sliding_feedback, R.drawable.sliding_infocenter };	public boolean isVisible = false;	public SlideMenuAdaper(Context mContext) {		inflater = (LayoutInflater) mContext.getSystemService(Context.LAYOUT_INFLATER_SERVICE);		SETTINGTOP_NAME = mContext.getResources().getStringArray(R.array.slidemenulist);	}	@Override	public int getCount() {		return SETTINGTOP_NAME.length;	}	public void setVersionVISIBLE(boolean isVisible) {		this.isVisible = isVisible;		notifyDataSetChanged();	}	@Override	public Object getItem(int position) {		return SETTINGTOP_NAME[position];	}	@Override	public long getItemId(int position) {		return position;	}	@SuppressLint("InflateParams")	@Override	public View getView(final int position, View convertView, ViewGroup parent) {		ViewHolder holder = null;		if (convertView == null) {			holder = new ViewHolder();			convertView = inflater.inflate(R.layout.setting_bottom_item, null);			holder.setting_bottom_text = (TextView) convertView.findViewById(R.id.setting_bottom_text);			holder.set_image = (ImageView) convertView.findViewById(R.id.set_image);			convertView.setTag(holder);		} else {			holder = (ViewHolder) convertView.getTag();		}		holder.setting_bottom_text.setText(SETTINGTOP_NAME[position]);		holder.set_image.setBackgroundResource(SETTINGTOP_IMAGE[position]);		return convertView;	}	private class ViewHolder {		private TextView setting_bottom_text;		private ImageView set_image;	}}